mapper.select:
select id, name, degree, type, threat_type as threatType, vendor, source, cve, cnnvd, description, notice, website, entity, created_time as createdTime, updated_time as updatedTime
from bug
where cve = #{cve}

mapper.update:
update bug
name = #{name}, degree = #{degree}, type = #{type}, threat_type = #{threatType}, vendor = #{vendor}, source = #{source}, cve = #{cve}, cnnvd = #{cnnvd}, description = #{description}, notice = #{notice}, website = #{website}, entity = #{entity}, created_time = #{createdTime}, updated_time = #{updatedTime},
where id = #{id}

mapper.select:
select id, name, degree, type, threat_type as threatType, vendor, source, cve, cnnvd, description, notice, website, entity, created_time as createdTime, updated_time as updatedTime
from bug
where cnnvd = #{cnnvd}
